import React, { Component } from 'react';
import {Row,Col,Button,Card,CardText,CardBody,CardTitle,CardSubtitle} from 'reactstrap';

class RaceCard extends Component {
    state = {
        isLoaded:false,
        races:[],
    }
    componentWillMount(){
        fetch('https://api-beta.open5e.com/races/?format=json')
            .then(res=>res.json())
            .then(json => {
                this.setState({
                    isLoaded:true,
                    races:json.results,
                })
            });
    }
    render() { 
        const {races} = this.state;
        const raceList = races.map(race, index =>{
            return (
                <Row key={index}>
                    <Card>
                        <CardBody>
                            <CardTitle>{race.name}</CardTitle>
                            <CardSubtitle>{race.desc}</CardSubtitle>
                            <CardText>
                                <div>{race.asi_desc}</div>
                            </CardText>
                            <Button>Select {race.name}</Button>
                        </CardBody>
                    </Card>
                </Row>
            );
        });
        return (
            <div>
                {raceList}
                {console.log(races)}
            </div>
        );
    }
}
 
export default RaceCard;
