import React, { Component } from 'react';
import {Row,Col,Button} from 'reactstrap';
class Race extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLoaded:false,
            races:[],
        }    
    }
    componentWillMount(){
        fetch('https://api-beta.open5e.com/races/?format=json')
            .then(res=>res.json())
            .then(json => {
                this.setState({
                    isLoaded:true,
                    races:json.results,
                })
            });
    }
    render() { 
        const {races} = this.state;
        const raceList = races.map(race =>{
            return (
                <li>{race.name}</li>
            );
        });
        return (
            <div>
                <Row>
                    <Col>
                        <h1>Choose Your Character's Race</h1>
                        <ul>
                            {raceList}
                        </ul>
                    </Col>
                </Row>
            </div>
        )
    }
}
 
export default Race;
