import React, { Component } from 'react';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {Row,Input,Col} from 'reactstrap';
class Health extends Component {
    state = {
        hitPoints:32,
        ac:14,
        maxHitPoints:32
    }
    render() { 
        return (
            <div className="row">
                <div id="health">
                    <div id="currentHitPoints">
                        <FontAwesomeIcon icon="plusCircle"/>
                        Hit Points: <span>{this.state.hitPoints}/{this.state.maxHitPoints}</span>
                    </div>
                    <div id="death-saves">
                        Death Saves:  
                        <input type="checkbox" name="1st Attempt" id="attempt1"/>
                        <input type="checkbox" name="2nd Attempt" id="attempt2"/>
                        <input type="checkbox" name="3rd Attempt" id="attempt3"/>
                    </div>
                </div>
                <div id="ac">
                    AC: {this.state.ac}
                </div>
                <div id="magic">

                </div>
            </div>
        );
    }
}
 
export default Health;
