import React, { Component } from 'react';
import {Row,Col,Button,Card,CardText,CardBody,CardTitle,CardSubtitle} from 'reactstrap';
class Race extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLoaded:false,
            races:[],
        }    
    }
    componentWillMount(){
        fetch('https://api-beta.open5e.com/races/?format=json')
            .then(res=>res.json())
            .then(json => {
                this.setState({
                    isLoaded:true,
                    races:json.results,
                })
            });
    }
    render() { 
        const {races} = this.state;
        const raceList = races.map(race =>{
            return (
                <Row>
                    <Card>
                        <CardBody>
                            <CardTitle>{race.name}</CardTitle>
                            <CardSubtitle>{race.desc}</CardSubtitle>
                            <CardText>
                                <div>{race.asi_desc}</div>
                            </CardText>
                            <Button>Select {race.name}</Button>
                        </CardBody>
                    </Card>
                </Row>
            );
        });
        return (
            <div>
                <Row>
                    <Col>
                        <h1>Choose Your Character's Race</h1>
                    </Col>
                </Row>
                {raceList}
                {console.log(races)}
            </div>
        )
    }
}
 
export default Race;
