import React, { Component } from 'react';
import {Row,Col,Button} from 'reactstrap';
class Race extends Component {
    constructor(props){
        super(props);
        this.state = {
            items:[],
            isLoaded:false,
            return:this.props.handler("home")
        }    
    }
    componentWillMount(){
        fetch('https://api-beta.open5e.com/races/?format=json')
            .then(res=>res.json())
            .then(json => {
                this.setState({
                    isLoaded:true,
                    items:json,
                })
            });
    }
    componentDidUpdate(){
        let races=this.state.items.results;
        if(races){
            console.log(races);
        }
    }
    render() { 
        return (
            <Row>
                <Col>
                    <h1>Choose Your Character's Race</h1>
                </Col>
            </Row>
        )
    }
}
 
export default Race;
