import React, { Component } from 'react';
import {Row,Col,Button} from 'reactstrap';
class Race extends Component {
    constructor(props){
        super(props);
        this.state = {
            isLoaded:false,
            races:[],
            return:this.props.handler("home")
        }    
    }
    componentWillMount(){
        fetch('https://api-beta.open5e.com/races/?format=json')
            .then(res=>res.json())
            .then(json => {
                this.setState({
                    isLoaded:true,
                    races:json.results,

                })
            });
    }
    pushRaces(array){
        if(array.length>0){
            return (
                <Row>
                    <Col>
                    </Col>
                </Row>
            );            
        }
    }
    render() { 
        return (
            <div>
                <Row>
                    <Col>
                        <h1>Choose Your Character's Race</h1>
                    </Col>
                </Row>
                {this.pushRaces(this.state.races)}
            </div>
        )
    }
}
 
export default Race;
